import {PRODUCT_DETAIL} from "../actions/action";

const iniState = {
    productDetail: {}
};

const Reducer = (state = iniState,action) => {
    if(action.type === PRODUCT_DETAIL){
        return{...state,productDetail: action.productDetail}
    }
};

// Default Export
export default Reducer;
