// Default imports
import axios from 'axios';

// CONSTANT ACTION
export const PRODUCT_DETAIL = 'PRODUCT_DETAIL';
export const PENDING_TRUE = 'PENDING_TRUE';
export const PENDING_FALSE = 'PENDING_FALSE';

// Function
function SetPendingTrue() {
    return {
        type: PENDING_TRUE
    }
}

function SetPendingFalse() {
    return {
        type: PENDING_FALSE
    }
}

function SetProductDetail(productDetail) {
    return {
        type: PRODUCT_DETAIL,
        productDetail: productDetail
    }
}

export function GetProductDetail() {
    return (dispatch) => {
        return axios
            .get('https://fe-assignment.vaimo.net/')
            .then(data => {
                dispatch(SetPendingTrue());
                dispatch(SetProductDetail(data.data));
                dispatch(SetPendingFalse());
            })
            .catch(error => console.log(error));
    }
}
