// Default imports
import React, {Component} from 'react';
import {connect} from "react-redux";
import './productDetail.css'

// Custom imports
import {GetProductDetail} from "../../actions/action";

class ProductDetail extends Component {
    // Constructor
    constructor(props) {
        super(props);
        this.handleInputChange = this.handleInputChange.bind(this);
    }


    // Form Load
    componentDidMount() {
        this.props.GetProductDetail();
    }

    renderProductDetails = () => {
        const info = this.props.productDetail;

        if (info) {
            const {product} = info;
            return (
                <div>
                    <img src={product.gallery[0].main} className='image'/>
                    <div>
                        <input type='checkbox' name='props' value='Ready to Ship'
                               checked={product.shipping.props.ready_to_ship === 'true'}
                               disabled={product.shipping.props.ready_to_ship === 'false'}
                               onChange={this.handleInputChange}/>
                    </div>
                </div>
            )
        }
    };

    render() {
        return (
            <div>
                {this.renderProductDetails()}
            </div>
        )
    }
}


// State management
const mapStateToProps = (state) => {
    return state;
};

// Default export
export default connect(mapStateToProps, {GetProductDetail})(ProductDetail);
