// Default imports
import React, {Component} from 'react';
import {connect} from "react-redux";

// Custom imports
import {GetProductDetail} from "../../actions/action";

class ProductDetail extends Component {
    // Form Load
    componentDidMount() {
        this.props.GetProductDetail();
    }

    renderProductDetails = () => {
        const info = this.props.productDetail;

        if (info) {
            const {product} = info;
            return (
                <div>Products info here:{product.name}</div>
            )
        }
    };

    renderLoader = () => {
        const {pending} = this.props;
        if (pending) {
            // Would've rendered a loader wheel here.
            return null
        } else {
            return (
                this.renderProductDetails()
            )
        }
    };

    render() {
        return (
            <div>
                {this.renderLoader()}
            </div>
        )
    }
}


// State management
const mapStateToProps = (state) => {
    return state;
};

// Default export
export default connect(mapStateToProps, {GetProductDetail})(ProductDetail);
